/* scss-combine:lib/functions */
// REM Calc
// scss-lint:disable ZeroUnit, NameFormat
$rem-base: 16px !default;

@function strip-unit($num) {
  @return $num / ($num * 0 + 1);
}

@function convert-to-rem($value, $base-value: $rem-base) {
  $value: strip-unit($value) / strip-unit($base-value) * 1rem;
  @if ($value == 0rem) { $value: 0; } // Turn 0rem into 0
  @return $value;
}

@function rem-calc($values, $base-value: $rem-base) {
  $max: length($values);
  @if $max == 1 { @return convert-to-rem(nth($values, 1), $base-value); }
  $remValues: ();
  @for $i from 1 through $max {
    $remValues: append($remValues, convert-to-rem(nth($values, $i), $base-value));
  }
  @return $remValues;
}

// Contrast Color Mix
@function contrast($color, $percent: 6%) {
  @if (lightness($color) > 65) {
    @return shade($color, $percent);
  } @else {
    @return tint($color, $percent);
  }
}

// Invert & Tint Color Mix
@function inversion($color, $shift: false, $percent: 30%) {
  $inverted-color: invert($color);
  $final-color: desaturate($inverted-color, 100%);

  @if ($shift == lighten) {
    @return lighten($final-color, $percent);
  } @else {
    @return darken($final-color, $percent);
  }
}


