/* scss-combine:general/general */
html { background-color: $footer-background-color; }

body {
  //scss-lint:disable ImportantRule
  margin-top: 0 !important;
  font-family: $body-font;
  font-size: $font-size;
  line-height: 1.666;
  color: $body-color;
  background-color: $body-background-color;

  &.scroll-locked {
    overflow: hidden;

    @include breakpoint(m-only) { overflow: visible; }
  }

  &.mobile-nav-open {
    position: fixed;
    width: 100%;
    height: 100%;
    overflow: hidden;

    @include breakpoint(l-up) {
      position: static;
      overflow: auto;
    }
  }
}

a {
  color: $accent-color;
  transition: color 0.2s $ease-out-quad;

  &:hover { @include hover($accent-color); }
}

// scss-lint:disable SingleLinePerSelector
h1, h2, h3, h4, h5, h6 {
  @extend %heading-text-treatment;
  line-height: 1.25;
}

h1 { font-size: 36px; }
h2 { font-size: 28px; }
h3 { font-size: 20px; }
h4 { font-size: 18px; }
h5 { font-size: 14px; }
h6 { font-size: 14px; }

p {
  margin-top: 15px;
  margin-bottom: 15px;
}

.page-title {
  //scss-lint:disable NameFormat
  @extend %heading-text-treatment, %wrapper, %full-width;
  font-family: $heading-font;
  font-size: $page-heading-size;

  @if (stencilFontFamily("heading-font") == "Lora") { font-weight: 600; }
  @if (stencilFontFamily("heading-font") == "Raleway") { font-weight: 300; }

  &.centered {
    display: inline-block;
    margin-top: 0;
    margin-bottom: $gutter * 2;
    color: $body-color;
    text-transform: none;

    @include breakpoint(l-only) { margin-bottom: $gutter * 2; }
    @include breakpoint(l-only) { margin-bottom: $gutter; }

    &::after {
      display: block;
      width: 100%;
      height: 1px;
      min-width: ($max-width / 10);
      margin-top: 15px;
      background-color: $border-color;
      content: "";
    }
  }
}

.section-title {
  //scss-lint:disable NameFormat
  @extend %section-title-text-treatment;
  padding-bottom: 35px;
  margin-top: 0;
  font-family: $section-heading-font;
  font-size: $heading-size;
  font-weight: 400;
  line-height: 1.2;
  letter-spacing: 3px;
  text-align: center;

  @if (stencilFontFamily("section-heading-font") == "Dosis") { font-weight: 700; }

  .has-description & { padding-bottom: 15px; }
}

.section-description {
  @include outer-container;
  text-align: center;

  > p {
    width: 65%;
    margin: 0 auto 60px;
  }
}

hr { background: $border-color; }

ol { padding-left: 1.5em; }

cite { color: $meta-color; }

button {
  border: 0;
  outline: 0;
}

.hidden { display: none; }

.sr-only { @extend .show-for-sr; }

/* scss-combine:general/scaffolding */
// Layout Blocks
.layout-block {
  padding-top: 75px;
  padding-bottom: 75px;
  -webkit-font-smoothing: antialiased;
  border-bottom: 1px solid $border-color;

  @include breakpoint(l-only) {
    padding-top: 50px;
    padding-bottom: 50px;
  }

  &.no-border {
    padding-bottom: 0;
    border-bottom: 0;

    +.layout-block {
      padding-top: 0;
    }
  }

  &:last-of-type {
    border-bottom: 0;
  }

  &.featured-products,
  &.popular-products,
  &.new-products,
  &.related-products {
    //scss-lint:disable SelectorDepth
    padding-bottom: calc(75px - #{$gutter}*4);

    @include breakpoint(l-only) { padding-bottom: calc(50px - #{$gutter}*3); }
    @include breakpoint(m-only) { padding-bottom: calc(50px - #{$gutter}*2); }
  }

  &.featured-products {
    background-color: $featured-products-background-color;

    .section-title,
    .product-grid-item-details h3 a,
    .product-grid-item-price,
    .price-tax-label { color: contrast($featured-products-background-color, 100%); }
  }

  &.popular-products {
    background-color: $popular-products-background-color;

    .section-title,
    .product-grid-item-details h3 a,
    .product-grid-item-price,
    .price-tax-label { color: contrast($popular-products-background-color, 100%); }
  }

  &.new-products {
    background-color: $new-products-background-color;

    .section-title,
    .product-grid-item-details h3 a,
    .product-grid-item-price,
    .price-tax-label { color: contrast($new-products-background-color, 100%); }
  }

  &.related-products {
    background-color: $related-products-background-color;

    .section-title,
    .product-grid-item-details h3 a,
    .product-grid-item-price,
    .price-tax-label { color: contrast($related-products-background-color, 100%); }
  }
}

.layout-wrapper { @include outer-container; }

/* scss-combine:general/pagination */
.pagination-container {
  @include outer-container;
  display: block;
  width: 100%;
  overflow: hidden;

  .pagination {
    position: relative;
    padding-top: $gutter * 2;
    padding-bottom: 12px;
    text-align: center;
    border-top: 1px solid $border-color;
  }
}

.pagination-list {
  display: inline-block;

  @include breakpoint(l-only) {
    position: relative;
    left: 50%;
    float: left;
  }
}

.pagination-number {
  display: inline-block;
  padding: 0 10px;
  vertical-align: top;

  @include media($max: $small) { display: none; }

  &.pagination-current {
    font-weight: bolder;
    font-size: 17px;
    border-left: 1px solid $border-color;
    border-right: 1px solid $border-color;
    padding: 0 15px;
  }

  a {
    color: $body-color;

    &:hover { @include hover($body-color); }
  }
}

.pagination-item {
  display: inline-block;
  font-size: 13px;
  line-height: 13px;
  letter-spacing: 2px;
  color: $body-color;
  text-align: center;
  text-transform: uppercase;
  transition: color 0.2s $ease-out-quad;

  &.next {
    position: absolute;
    top: $gutter * 2;
    right: 0;
    padding-left: 12px;

    i {
      padding-left: 5px;
    }
  }

  &.previous {
    position: absolute;
    top: $gutter * 2;
    left: 0;
    padding-right: 12px;

    i {
      padding-right: 5px;
    }
  }

  span {
    display: inline-block;
  }

  &.current a {
    color: $light-color;
    background-color: $accent-color;
    border-color: $accent-color;
  }

  i {
    color: $dark-color;
  }
}

/* scss-combine:general/button */
.button {
  padding: 13px 20px;
  font-family: $button-font;
  font-size: 13px;
  font-weight: 400;
  line-height: 13px;
  letter-spacing: 0.1em;
  color: $primary-button-text-color;
  text-transform: uppercase;
  background-color: $primary-button-background-color;
  border: 1px solid $primary-button-background-color;
  border-radius: 3px;
  transition: all 0.3s $ease-out-quad;

  &:hover {
    color: $primary-button-text-color;
    background-color: $primary-button-hover;
    border-color: $primary-button-hover;
  }

  &.button-alt {
    font-weight: 600;
    color: $secondary-button-text-color;
    background-color: $body-background-color;
    border-color: $secondary-button-background-color;

    &:hover {
      color: $secondary-button-hover;
      border-color: $secondary-button-hover;
    }
  }

  &.disabled {
    color: $disabled-button-text-color;
    cursor: default;
    background-color: $disabled-button-background-color;
    opacity: 0.5;

    + .spinner { display: none; }
  }

  &.button-small {
    padding: 8px 12px;
    font-size: 12px;
  }

  &.button-large {
    padding: 20px 40px;
    font-size: 16px;
  }

  &:focus { outline: none; }
}

.text-button {
  padding: 0;
  font-size: $meta-size;
  letter-spacing: 0.1em;
  color: $primary-button-background-color;
  text-transform: uppercase;
  background: none;
  border: 0;
  outline: 0;
  transition: all 0.3s $ease-out-quad;

  &:hover { color: $primary-button-hover; }
}

/* scss-combine:general/form */
// Forms
.form-input {
  display: block;
  padding: 5px 10px;
  line-height: 1.8;
  color: $input-color;
  background: $input-background-color;
  border-color: $input-border-color;
  border-radius: 3px;
  transition: border-color 0.2s $ease-out-quad;

  &::placeholder { color: lighten($input-color,20%); }

  &:focus { outline: none; }

  &[type='text'],
  &[type='email'],
  &[type='number'],
  &[type='password'] {
    height: 43px;
    min-width: 220px;

    @include breakpoint(xl-only) { min-width: 180px; }
  }

  &[type='number'] {
    width: 75px;
    min-width: 0;
  }

  &.bottom-border {
    position: relative;
    top: -1px;
    border: 0;
    border-bottom: 1px solid $input-border-color;

    &:focus { border-bottom-color: $accent-color; }
  }
}

.form-field {
  textarea { width: 100%; }
}

// Form Elements
.form-error {
  color: $error-color;
  border: 1px solid $error-color;

  &::placeholder { color: lighten($error-color,30%); }
}

.form-error-message { color: $error-color; }

// Select wrapping
.form-select-wrapper {
  $input-height: 43px;
  position: relative;
  display: block;
  width: 100%;
  height: $input-height;
  min-width: 220px;
  padding: 0 15px;
  font-size: $font-size;
  line-height: $input-height - 2px;
  color: lighten($input-color, 20%);
  text-align: left;
  cursor: pointer;
  background-color: $input-background-color;
  border: 1px solid $input-border-color;

  @include breakpoint(xl-only) { min-width: 180px; }

  &:after {
    position: absolute;
    top: 50%;
    right: 17px;
    width: 7px;
    height: 7px;
    margin-top: -5px;
    border-top: 1px solid $heading-color;
    border-right: 1px solid $heading-color;
    content: "";
    transform: rotate(135deg);
  }

  select {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 2;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;
    appearance: none;
  }

  option { color: $body-color; }
}

// File Wrapper
.form-file-wrapper {
  position: relative;
  display: block;
  width: 100%;
  padding: 0;
  overflow: hidden;
  font-size: $font-size;
  line-height: normal;
  color: lighten($input-color,20%);
  text-align: left;
  cursor: pointer;
  background-color: $input-background-color;
  border: 1px solid $input-border-color;

  &:before {
    @extend .button;
    position: absolute;
    top: 0;
    right: 0;
    height: 51px;
    padding: 0 30px;
    line-height: 49px;
    background-color: $accent-color;
    border-radius: 0 3px 3px 0;
    content: attr(data-button-label);
  }

  &:hover:before { background-color: lighten($accent-color, 10%); }

  .form-file {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 2;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;
    appearance: none;
  }
}

.form-file-types {
  display: block;
  margin-top: 5px;
  font-size: $meta-size;
  font-style: italic;
  color: $meta-color;
}

// Radio / Checkbox
.form-field-radio,
.form-field-checkbox {

  .form-radio,
  .form-checkbox { @extend .show-for-sr; }

  .form-label-text {
    position: relative;

    &:before {
      position: relative;
      top: 4px;
      display: inline-block;
      width: 20px;
      height: 20px;
      margin-right: 10px;
      background-color: $input-background-color;
      border: 1px solid $input-border-color;
      content: "";
    }

    &:after {
      position: absolute;
      content: "";
    }
  }
}

.form-field-radio {
  .form-label-text {
    &:before { border-radius: 100%; }

    &:after {
      top: 10px;
      left: 6px;
      width: 8px;
      height: 8px;
      border-radius: 100%;
    }
  }

  .form-radio:checked + .form-label-text:after { background-color: $accent-color; }
}

.form-field-checkbox {
  .form-label-text:after {
    top: 8px;
    left: 7px;
    width: 6px;
    height: 9px;
    transform: rotate(45deg);
  }

  .form-checkbox:checked + .form-label-text:after {
    border: solid $accent-color;
    border-width: 0 2px 2px 0;
  }
}

// General Page Forms (contact, gift certificates, etc)
.page-form {
  .form { margin: $gutter 0; }

  .form-field {
    float: left;
    width: 100%;
    margin-top: 20px;

    &:first-of-type { margin-bottom: 0; }

    .form-input { width: 100%; }

    .form-inline-message {
      float: left;
      width: 100%;
      padding: 5px 0 15px;
      font-size: $meta-size;
      color: $error-color;
    }
  }

  .form-field-control {
    float: left;
    width: 100%;
  }

  .form-field-title {
    float: left;
    width: 100%;
    margin-bottom: 6px;
    font-weight: normal;
  }

  .form-actions {
    float: left;
    margin: $gutter 0;

    .button {
      display: inline-block;
      margin: 0 10px $gutter 0;

      @include breakpoint(m-only) {
        width: 100%;
        margin: 5px 0;

        //scss-lint:disable NestingDepth
        &:nth-child(2) {
          @extend .button-alt;
        }
      }
    }
  }

  .form-field-captcha {
    //scss-lint:disable NestingDepth, SelectorDepth
    float: left;
    width: 100%;
    margin-bottom: 15px;

    @include breakpoint(m-only) {
      font-size: 0;

      .form-label,
      .captcha-note { font-size: $font-size; }
    }

    .captcha-image {
      display: inline-block;
      padding: 5px;
      margin: 15px 15px 0 0;
      vertical-align: middle;
      background-color: rgba($input-border-color, 0.5);
      border: 1px solid $input-border-color;
      border-radius: 4px;

      @include breakpoint(m-only) {
        width: 48%;
        margin-right: 4%;
      }

      img {
        display: block;
        height: 75px;

        @include breakpoint(m-only) {
          width: 100%;
          height: auto;
        }
      }
    }

    .form-field-control {
      display: inline-block;
      float: none;
      width: auto;
      margin-right: 0;
      vertical-align: middle;

      @include breakpoint(m-only) { width: 48%; }

      .form-input {
        width: 150px;
        min-width: 0;

        @include breakpoint(m-only) {
          width: 100%;
          font-size: $font-size;
        }
      }
    }

    + .form-field { @extend %clearfix; }
  }
}

/* scss-combine:general/table */
table {
  color: $body-color;
  border-color: $border-color;
}

th {
  @extend %heading-text-treatment;
  color: $input-color;
  background: $input-background-color;
}

/* scss-combine:general/modals */
.modal-overlay { background-color: rgba($accent-color, 0.85); }

.modal {
  max-width: 1060px;
  max-height: 90%;
  padding: $gutter * 2;
  overflow-y: scroll;
  background: $light-color;

  @include breakpoint(l-only) {
    margin-right: 20px;
    margin-left: 20px;
  }

  // scss-lint:disable ImportantRule
  @include breakpoint(s-only) { margin-top: 20px !important; }
}

.modal-content {
  position: relative;
  background: $body-background-color;

  // scss-lint:disable VariableForProperty
  .modal-close {
    position: absolute;
    top: 0;
    right: 0;
    font-size: $font-size;
    line-height: $font-size;
    color: rgba($body-color, 0.5);

    // scss-lint:disable VariableForProperty
    &:hover { color: rgba($body-color, 0.8); }
  }
}

/* scss-combine:general/rte */
// RTE Page Content
.rte {
  ol {
    padding-left: rem-calc(35);
    margin-bottom: rem-calc(20);
  }

  ul {
    padding-left: rem-calc(35);
    margin-bottom: rem-calc(20);
    list-style: disc;
  }

  img {
    display: block;
    max-width: 100%;
    height: auto;
  }

  .page-feed {
    padding: 0;
    margin: ($gutter * 2) 0;
    list-style: none;
    border: 1px solid $border-color;

    h5 {
      display: inline-block;
      margin: 0;
      border-bottom: 1px solid $heading-color;
    }

    li {
      //scss-lint:disable NestingDepth
      padding: $gutter;

      &:nth-child(even) {
        background-color: darken($body-background-color, 3%);
      }

      > *:last-child { margin-bottom: 0; }
    }
  }
}

/* scss-combine:general/loading */
.spinner {
  //scss-lint:disable VendorPrefix, NestingDepth
  position: absolute;
  top: 50%;
  width: 26px;
  height: 26px;
  margin: -13px 0 0 -13px;
  transition: right 0.4s $ease-out-quad;
  -webkit-backface-visibility: hidden;

  .quick-shop-wrapper > & {
    position: absolute;
    left: 50%;
    width: 46px;
    height: 46px;
    margin: -23px 0 0 -23px;
  }

  .disabled & { display: none; }

  .product-add-button-wrapper & {
    right: 20px;
    left: auto;
    z-index: -1;

    &.visible {
      right: -30px;

      .quick-shop & { right: -40px; }
    }
  }

  .spinner-circle {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    -webkit-backface-visibility: hidden;

    &:before {
      display: block;
      width: 11%;
      height: 22%;
      margin: 0 auto;
      background-color: $accent-color;
      border-radius: 100%;
      content: "";
      animation: spinner-circleFadeDelay 1.2s infinite ease-in-out both;
      -webkit-backface-visibility: hidden;

      .quick-shop-wrapper > & {
        width: 6%;
        background-color: mix($accent-color, $light-color, 50%);
      }
    }

    &.spinner-circle2    { transform: rotate(30deg); }
    &.spinner-circle3    { transform: rotate(60deg); }
    &.spinner-circle4    { transform: rotate(90deg); }
    &.spinner-circle5    { transform: rotate(120deg); }
    &.spinner-circle6    { transform: rotate(150deg); }
    &.spinner-circle7    { transform: rotate(180deg); }
    &.spinner-circle8    { transform: rotate(210deg); }
    &.spinner-circle9    { transform: rotate(240deg); }
    &.spinner-circle10   { transform: rotate(270deg); }
    &.spinner-circle11   { transform: rotate(300deg); }
    &.spinner-circle12   { transform: rotate(330deg); }

    &.spinner-circle2:before   { animation-delay: -1.1s; }
    &.spinner-circle3:before   { animation-delay: -1s; }
    &.spinner-circle4:before   { animation-delay: -0.9s; }
    &.spinner-circle5:before   { animation-delay: -0.8s; }
    &.spinner-circle6:before   { animation-delay: -0.7s; }
    &.spinner-circle7:before   { animation-delay: -0.6s; }
    &.spinner-circle8:before   { animation-delay: -0.5s; }
    &.spinner-circle9:before   { animation-delay: -0.4s; }
    &.spinner-circle10:before  { animation-delay: -0.3s; }
    &.spinner-circle11:before  { animation-delay: -0.2s; }
    &.spinner-circle12:before  { animation-delay: -0.1s; }
  }
}

@keyframes spinner-circleFadeDelay {
  0%, 39%, 100% { opacity: 0; }
  40% { opacity: 1; }
}


// Module overrides
/* scss-combine:modules/flickity */
// *! Flickity v1.1.1
// http://flickity.metafizzy.co

.flickity-enabled {
  position: relative;

  &.is-draggable {
    user-select: none;
    tap-highlight-color: transparent;

    .flickity-viewport {
      //scss-lint:disable DuplicateProperty, NestingDepth
      cursor: move;
      cursor: grab;

      &.is-pointer-down { cursor: grabbing; }
    }
  }

  &:focus { outline: none; }
}

.flickity-viewport {
  position: relative;
  height: 100%;
  overflow: hidden;
}

.flickity-slider {
  position: absolute;
  width: 100%;
  height: 100%;
}

.flickity-prev-next-button {
  position: absolute;
  top: 50%;
  width: 45px;
  height: 45px;
  cursor: pointer;
  background: transparent;
  background-color: $light-color;
  border: 0;
  border-radius: 50%;
  transform: translateY(-50%);

  &:hover { background: transparent; }
  &.previous { left: 10px; }
  &.next { right: 10px; }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 5px $accent-color;
  }

  &:active { opacity: 0.6; }

  &:disabled {
    cursor: auto;
    opacity: 0.3;
  }

  svg {
    position: absolute;
    top: 20%;
    left: 20%;
    width: 60%;
    height: 60%;
  }

  .arrow { fill: $dark-color; }

  .no-svg {
    font-size: 25px;
    color: $dark-color;
  }
}

/* scss-combine:modules/carousel */
// Carousel
.carousel-wrap {
  padding: 0 0 ($gutter * 2);
  background-color: transparent;
  border-bottom-width: 0;

  @include breakpoint(l-only) { padding-bottom: 0; }

  &.slide-fixed {
    padding-bottom: 0;
    background-color: $carousel-background-color;
    border-bottom-width: 1px;

    @include breakpoint(l-only) { border-bottom-width: 0; }
  }
}

.carousel {
  position: relative;
  width: 100%;
  transition: all 0.3s $ease-out-quad;

  @include breakpoint(m-only) {
    // scss-lint:disable ImportantRule
    height: auto !important;
    margin-bottom: 0;
    background-color: darken($body-background-color, 3%);
  }

  .slide-fixed & {
    @include outer-container;
    padding: $gutter * 2;

    @include breakpoint(l-only) {
      max-width: 100%;
      padding: 0;
      margin-top: 0;
    }
  }

  img { display: none; }
}

.flickity-viewport {
  height: 0;
  overflow: hidden;
  transition: height 0.4s $ease-out-quad;

  @include breakpoint(m-only) { margin-bottom: 0; }

  .slide-fixed & { margin-bottom: 0; }

  img { display: block; }
}

.carousel-slides {
  *,
  ~ * {
    opacity: 0;
    visibility: hidden;
  }

  &.flickity-enabled *,
  &.flickity-enabled ~ * {
    opacity: 1;
    visibility: visible;
  }
}

// Carousel Item
.carousel-item {
  position: relative;
  display: block;
  width: 100%;
  transition: transform 0.4s $ease-out-quad;

  &.is-selected {
    //scss-lint:disable NestingDepth
    + .carousel-item {
      height: 100%;

      .carousel-item-image { display: none; }

      img,
      .carousel-item-image { height: 100%; }
    }
  }

  figure { width: 100%; }
  img { width: 100%; }
}

// Caption
.carousel-item-info {
  position: absolute;
  top: 0;
  width: 100%;
  height: 100%;
  transition: all 0.3s $ease-out-quad;

  @include breakpoint(m-only) {
    position: relative;
    background-color: darken($body-background-color, 5%);
    border-top: 0;
  }
}

.carousel-item-info-container {
  display: table;
  width: 100%;
  height: 100%;
}

.carousel-item-info-align {
  display: table-cell;
  text-align: center;
  vertical-align: middle;

  > :first-child { margin-top: 30px; }
  > :last-child { margin-bottom: 33px; }
}

.carousel-item-heading {
  //scss-lint:disable NameFormat
  width: 100%;
  padding: 0 10%;
  margin: $gutter auto;
  font-family: $carousel-heading-font;
  font-size: $carousel-heading-font-size;
  font-weight: 400;
  line-height: 1;
  color: $carousel-color;
  text-transform: none;
  transform: translateY(-10px);
  transition: all 0.7s $ease-out-quad;
  transition-delay: 0.3s;

  @if (stencilFontFamily("carousel-heading-font") == "Dosis") {
    font-weight: 700;
    text-transform: uppercase;
  }

  @if (stencilFontFamily("carousel-heading-font") == "Raleway") { font-weight: 300; }

  @include breakpoint(xl-only) { font-size: 55px; }
  @include breakpoint(l-only) { font-size: 45px; }

  @include breakpoint(m-only) {
    position: relative;
    display: inline-block;
    padding: 0 4%;
    margin: ($gutter / 2) auto;
    font-size: 25px;
    color: $dark-color;
    transform: translateY(0);
  }

  @include breakpoint(s-only) { font-size: 20px; }

  .slide-fixed & {
    font-size: 45px;

    @include breakpoint(xl-only)  { font-size: $carousel-heading-font-size / 1.3; }
    @include breakpoint(l-only)   { font-size: $carousel-heading-font-size / 1.6; }
    @include breakpoint(m-only)   { font-size: 25px; }
  }

  .is-selected & { transform: translateY(0); }
}

.carousel-item-text {
  //scss-lint:disable NameFormat
  width: 100%;
  padding: 0 10%;
  margin: $gutter auto;
  font-family: $carousel-caption-font;
  font-size: $carousel-caption-font-size;
  font-weight: 300;
  line-height: 1;
  letter-spacing: 0.05em;
  color: $carousel-color;
  transform: translateY(10px);
  transition: all 0.7s $ease-out-quad;
  transition-delay: 0.3s;

  @if (stencilFontFamily("carousel-caption-font") == "Lora") {
    font-weight: 700;
    text-transform: uppercase;
  }

  @if (stencilFontFamily("carousel-caption-font") == "Raleway") { font-weight: 300; }

  @include breakpoint(xl-only) { font-size: $carousel-caption-font-size / 1.3; }
  @include breakpoint(l-only) { font-size: $carousel-caption-font-size / 1.6; }
  @include breakpoint(m-only) { font-size: 17px; }

  @include breakpoint(m-only) {
    display: inline-block;
    padding: 0 4%;
    margin: ($gutter / 2) auto;
    font-size: 15px;
    color: $dark-color;
    transform: translateY(0);
  }

  .is-selected & { transform: translateY(0); }
}

.carousel-item-button {
  padding: 23px 55px;
  margin: $gutter auto;
  color: $carousel-button-color;
  background-color: $carousel-button-background-color;
  border-color: $carousel-button-background-color;
  transform: translateY(30px);
  transition: transform 0.7s $ease-out-quad 0.3s, background-color 0.4s $ease-out-quad, border-color 0.4s $ease-out-quad, color 0.4s $ease-out-quad;

  @include breakpoint(xl-only) { padding: 19px 45px; }
  @include breakpoint(l-only) { padding: 15px 35px; }

  @include breakpoint(m-only) {
    padding: 12px 20px;
    margin: ($gutter / 2) auto;
    transform: translateY(0);
  }

  &:hover {
    color: $carousel-button-color;
    background-color: darken($carousel-button-background-color, 10%);
    border-color: darken($carousel-button-background-color, 10%);
  }

  .is-selected & { transform: translateY(0); }
}

// Nav Arrows
.carousel-navigation {
  position: absolute;
  top: 50%;
  left: 0;
  z-index: 30;
  width: 100%;
  margin-top: -35px;

  @include breakpoint(m-only) {
    top: 20px;
    margin-top: 0;
  }
}

.carousel-navigation-item {
  position: absolute;
  font-size: 50px;
  color: $carousel-nav-color;
  cursor: pointer;
  transition: color 0.4s $ease-out-quad;

  @include breakpoint(m-only) { font-size: 30px; }

  @include breakpoint(l-up) {
    &:hover { color: $accent-color; }
  }

  &.carousel-previous {
    left: 50px;

    @include breakpoint(l-only) { left: 15px; }
  }

  &.carousel-next {
    right: 50px;

    @include breakpoint(l-only) { right: 15px; }
  }
}

// Pagination
.flickity-page-dots {
  position: relative;
  bottom: 0;
  width: 100%;
  padding: 0;
  margin-top: 25px;
  text-align: center;

  @include breakpoint(m-only) { display: none; }

  .slide-fixed & {
    position: absolute;
    bottom: 25px;
    margin-top: 0;
  }

  .dot {
    display: inline-block;
    width: rem-calc(10);
    height: rem-calc(10);
    margin: rem-calc(0 5);
    cursor: pointer;
    background-color: rgba($accent-color, 0.25);
    border-radius: 50%;

    .slide-fixed & { background-color: rgba($light-color, 0.4); }

    &.is-selected {
      //scss-lint:disable NestingDepth
      background-color: $accent-color;

      .slide-fixed & { background-color: $light-color; }
    }
  }
}

// Optional Fade Transition
.fade-transition {
  //scss-lint:disable ImportantRule
  .flickity-slider {
    background-color: $body-background-color;
    transform: none !important;
  }

  .carousel-item {
    left: 0 !important;
    background-color: $body-background-color;
    opacity: 0;
    transition: opacity 0.6s ease-in-out;

    &.is-selected {
      z-index: 10; 
      opacity: 1;
    }
  }
}

/* scss-combine:modules/tabs */
.tab-title {
  display: inline-block;
  margin-right: 20px;
  vertical-align: middle;

  &:last-child {
    margin-right: 0;
  }

  &.active > a {
    color: $accent-color;
    border-bottom-color: $accent-color;
  }

  > a {
    display: inline-block;
    padding-bottom: 6px;
    color: $heading-color;
    border-bottom-color: transparent;
    border-bottom-style: solid;
    border-bottom-width: 1px;

    &:hover {
      color: $accent-color;
    }
  }
}

.tabs-content-panel {
  display: none;

  &.active {
    display: block;
  }
}

.tabs-content {
  padding-top: $gutter * 2;
  padding-bottom: $gutter * 2;

  .quick-shop & {
    padding-bottom: 0;
  }

  p {
    margin-top: 0;
  }
}


// Accounts
/* scss-combine:account/variables */
$account-link-color: $navigation-color;
$account-light-color: $light-color;
$account-accent-color: $accent-color;
$account-border-color: $border-color;
$account-gutter: $gutter*2;
$account-font-size: $font-size;
$account-border-radius: 4px;
$account-text-color: $body-color;
$account-card-background-color: $body-background-color;
$account-alert-error-color: $error-color;
$account-alert-success-color: $success-color;

/* scss-combine:core/account */
// Default Variables

$account-link-color: #888 !default;
$account-light-color: #fff !default;
$account-accent-color: #5dc5e3 !default;
$account-border-color: #e4e4e4 !default;
$account-gutter: 30px !default;
$account-font-size: 15px !default;
$account-border-radius: 0 !default;
$account-text-color: #5f5f5f !default;

$account-card-background-color: #f5f5f5 !default;

$giftcard-nav-use-default-styles: true !default;

$account-breakpoint-small: 620px !default; // menu changes from full-width to column
$account-breakpoint-medium: 900px !default; // menu changes from 35% to 25% width

// Internal Variables

$account-font-size-heading: $account-font-size + 2 !default;

$account-text-color-light: lighten($account-text-color,20%) !default;

$account-gutter-inner: $account-gutter / 2 !default;

$auth-gutter: $account-gutter !default;
$auth-font-size: $account-font-size !default;

// Imports

/* scss-combine:account/components/layout */
.account-wrapper {
  @extend %clearfix;
  font-size: $account-font-size;
}

.account-nav,
.account-body {
  vertical-align: top;
}

.account-body {
  @media screen and (min-width: $account-breakpoint-small) {
    float: right;
    width: 65%;
    padding-left: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 75%;
  }

  > :first-child {
    margin-top: 0;
  }

  .account-nav-hidden & {
    float: none;
    width: 100%;
    padding-left: 0;
    margin-left: 0;
  }
}

.account-nav {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    float: left;
    width: 35%;
    padding-right: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 25%;
  }
}

/* scss-combine:account/components/forms */
.account-wrapper {
  .form-label-text {
    display: inline-block;
    width: 100%;
    margin-bottom: 5px;
  }

  .form-field-checkbox,
  .form-field-radio {
    .form-label-text { width: auto; }
  }

  .form-field {
    margin-bottom: $account-gutter-inner;
  }

  .form-input {
    width: 100%;

    &.account-form-half {
      width: 50%;
    }
  }

  .form-actions {
    clear: both;
  }
}

.account-form-column-container {
  @extend %clearfix;

  .form-field {
    @media screen and (min-width: 480px) {
      float: left;
      width: 50%;

      &:nth-child(odd) { padding-right: $account-gutter-inner; }
      &:nth-child(even) { padding-left: $account-gutter-inner; }
    }
  }
}

/* scss-combine:account/components/table */
.account-wrapper {
  table {
    width: 100%;
    margin-top: $account-gutter-inner;
    margin-bottom: $account-gutter-inner;
  }

  td,
  th {
    padding: $account-gutter-inner;
  }
}

.account-table-tight {
  th,
  td {
    padding: 10px 0;
    border-bottom: 1px solid $account-border-color;
  }
}

/* scss-combine:account/components/list */
// Layout
// Each list item has 3 sections: thumbnail, body, and badge.

.account-items {
  margin-bottom: $account-gutter;
}

.account-item {
  position: relative;
  display: table;
  width: 100%;
  border-bottom: 1px solid $account-border-color;

  &:first-of-type {
    border-top: 1px solid $account-border-color;
  }
}

.account-item-thumbnail,
.account-item-body,
.account-item-badge,
.account-item-actions {
  display: table-cell;
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;
  vertical-align: top;
}

// Link
// Makes the entire item clickable

.account-item-link {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
}

// Thumbnail

.account-item-thumbnail {
  min-width: 100px;

  img {
    height: auto;
    max-width: 100px;
  }
}

// Body

.account-item-body {
  width: 100%;

  .account-item-thumbnail + & {
    padding-left: $account-gutter-inner;
  }
}

.account-item-title {
  margin-top: 3px;
  font-size: $account-font-size;

  a {
    color: inherit;
  }
}

.account-item-title-large {
  font-size: $account-font-size * 1.2;
}

.account-item-description {
  margin-top: 0;
  margin-bottom: 5px;
  color: $account-text-color;
}

.account-item-details {
  @extend %clearfix;
  font-size: $account-font-size;
  color: $account-text-color;
}

.account-item-detail {
  float: left;
  margin-top: $account-gutter-inner;
  margin-right: $account-gutter;
}

//scss-lint:disable VariableForProperty
.account-item-detail-title {
  display: block;
  margin-top: 0;
  margin-bottom: 3px;
  font-size: 12px;
  font-weight: normal;
  letter-spacing: 1px;
  color: mix($account-light-color,$account-text-color,60%);
  text-transform: uppercase;
}

// Badge

.account-item-badge,
.account-item-actions {
  white-space: nowrap;
}

/* scss-combine:account/components/header */
.account-header {
  display: table;
  width: 100%;
  margin-bottom: $account-gutter-inner;
}

.account-heading,
.account-toolbar {
  display: table-cell;
  vertical-align: baseline;
}

.account-toolbar {
  text-align: right;

  * {
    display: inline;
  }
}

.account-tool {
  position: relative;
  z-index: 10;
  display: inline-block;
  padding: 0;
  background: transparent;
  margin-left: 10px;
  cursor: pointer;
  color: inherit;
  border: 0;

  &:hover { color: $account-link-color; }

  &:focus { outline: 0; }
}

/* scss-combine:account/components/navigation */
.account-nav-list {
  padding: 0;
  margin-bottom: $account-gutter-inner;
  list-style: none;
  border: 1px solid $account-border-color;
  border-radius: $account-border-radius;
}

.account-nav-item {
  color: $account-link-color;
  border-top: 1px solid $account-border-color;

  &:first-child {
    border-top: 0;
  }

  &.is-active {
    color: $account-accent-color;
    border-left: 3px solid $account-accent-color;
  }
}

.account-nav-link {
  display: block;
  padding: 15px;
  color: inherit;
}

.account-nav-badge {
  float: right;
}

/* scss-combine:account/components/pagination */
.account-wrapper .pagination {
  margin-top: $account-gutter-inner;
}

/* scss-combine:account/components/card */
%account-card-style {
  background: $account-card-background-color;

  .form-input {
    background-color: $account-light-color;
  }
}

.account-card {
  @extend %account-card-style;
  padding: $account-gutter-inner;
  margin: $account-gutter-inner 0;
  border-radius: $account-border-radius;
}

/* scss-combine:account/components/toggle */
// Set the initial toggle state to hidden
.account-toggle-hidden {
  display: none;
}

/* scss-combine:account/components/status */
// Status
// A label used to mark the status of an item

.account-status {
  padding: 6px 10px 5px;
  margin: 0;
  font-size: 12px;
  font-weight: normal;
  line-height: 1;
  letter-spacing: 1px;
  color: $account-light-color;
  text-transform: uppercase;
  background: #8e8e8e; // TODO: Colors for each status
}

/* scss-combine:account/components/shipments */
// Shipments
// A list of shipments made for an order. Shown on the Order Details page.

.account-shipments {
  margin-bottom: $account-gutter;
}

.account-shipments-table {
  th,
  td {
    padding: 10px 0;
    text-align: left;
  }

  th {
    padding: $account-gutter-inner 0 0;
    border-top: 1px solid $account-border-color;
  }

  // Override default margins
  .account-wrapper & {
    margin-top: 0;
    margin-bottom: 0;
  }
}


/* scss-combine:account/pages/address */
// Address List

.account-page-add-address {
  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-address-detail {
  display: block;
}

// Add Address

.account-address-form {
  padding-top: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-medium) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/orders-receipt */
.account-page-orders-receipt .account-content {
  padding-top: $account-gutter-inner;
  border-top: 1px solid $account-border-color;
}

// Summary

.account-receipt-summaries {
  @extend %clearfix;
}

.account-receipt-summary {
  float: left;
  width: 33%;
  padding-right: $account-gutter;
}

.account-receipt-summary-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-receipt-summary-address {
  line-height: 1.5;
}

.account-receipt-detail {
  margin-bottom: 5px;
}

.account-receipt-detail-title {
  color: $account-text-color-light;
}

// Comments

.account-receipt-comments {
  white-space: pre-line;
}

// Receipt Items (products)

.account-receipt-items {
  padding-top: $account-gutter-inner;
}

.account-receipt-download {
  margin: 10px 0;
}

// Totals

.account-receipt-totals {
  display: table;
  width: 50%;
  margin-left: 50%;
}

.account-receipt-total {
  display: table-row;
  margin-bottom: $account-gutter-inner;
}

.account-receipt-total-title,
.account-receipt-total-value {
  display: table-cell;
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;
  border-top: 1px solid $account-border-color;

  .account-receipt-total:first-of-type & {
    border-top: 0;
  }
}

.account-receipt-total-title {
  font-weight: normal;
  letter-spacing: 1px;
  color: $account-text-color-light;
}

.account-receipt-total-value {
  text-align: right;
}

/* scss-combine:account/pages/recent-items */

/* scss-combine:account/pages/returns */
// Return form

.account-return-table {
  th {
    text-align: left;

    &:last-of-type {
      text-align: right;
    }
  }

  .form-field {
    margin-bottom: 0;
  }
}

.account-return-fields {
  margin-top: $account-gutter;

  .form-field-select {
    width: 50%;
  }
}

/* scss-combine:account/pages/wishlists */
.account-wishlists {
  margin-bottom: 30px;
}

/* scss-combine:account/pages/messages */
// Listing

.account-messages-table.table-clean {
  border-bottom: 1px solid $account-border-color;

  td {
    padding-right: 0;
    padding-left: 0;
    vertical-align: top;
    border-top: 1px solid $account-border-color;
  }
}

.account-messages-table-date {
  width: 22%;
  text-align: right;
}

.account-message-subject {
  margin-top: 0;
  margin-bottom: 10px;
  font-size: $account-font-size-heading;
  font-weight: 500;

  .account-message-is-reply &:before {
    padding-right: 5px;
    content: "\21c9";
  }

  .account-message-is-read & {
    color: $account-text-color-light;
  }
}

.account-message-content {
  padding-right: $account-gutter;
  font-size: $account-font-size;
  line-height: 1.5;
  color: $account-text-color;
  white-space: pre-line;

  .account-message-is-read & {
    color: lighten($account-text-color-light,10%);
  }
}

.account-message-date {
  color: $account-text-color-light;
}

// Form

.account-messages-form-subject {
  width: 50%;
}

/* scss-combine:account/pages/download */
// List

.account-download {
  padding: $account-gutter-inner 0 0;
  margin: $account-gutter-inner 0;
  border-top: 1px solid $account-border-color;
}

.account-download-title {
  margin: 0;
  font-size: 1em;
}

.account-download-link {
  color: inherit;
  text-decoration: underline;
}

.account-download-filename {
  font-size: 1.2em;
}

.account-download-filesize {
  padding-left: 5px;
  font-weight: normal;
  color: $account-text-color-light;
}

.account-download-description {
  padding-top: 10px;
  padding-bottom: 10px;
}

.account-download-expiry {
  font-size: 0.9em;
  color: $account-text-color-light;
}

.account-download-expiry-item {
  display: block;
  padding-bottom: 5px;

  &:last-of-type {
    padding-bottom: 0;
  }
}

// Gallery

.account-downloads-gallery-image {
  text-align: center;
}

.account-downloads-gallery-thumbs {
  @extend %clearfix;
  text-align: center;
}

.account-downloads-gallery-thumb {
  display: inline-block;
  width: 10%;
  height: 10%;
  padding: 0 2px;
  vertical-align: middle;
  cursor: pointer;

  img {
    width: 100%;
    height: auto;
  }
}

/* scss-combine:account/pages/invoice */
.account-invoice-date {
  margin-top: -20px;
  font-size: $account-font-size;
}

// Address

.account-invoice-addresses {
  @extend %clearfix;
  margin-top: $account-gutter;
  margin-bottom: $account-gutter;
}

.account-invoice-address {
  float: left;
  margin-right: $account-gutter;
}

.account-invoice-address-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-invoice-address-body {
  line-height: 1.5;
}

// List

.account-invoice-table {
  .account-wrapper & {
    margin-bottom: 0;
  }

  th,
  td {
    padding-right: $account-gutter-inner;
    text-align: left;
    vertical-align: top;

    &:nth-of-type(0n+3) {
      width: 10%;
    }

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }

  td:first-of-type {
    width: 60px + 25px;
  }
}

.account-invoice-item-thumbnail {
  width: 60px;
  height: auto;
}

.account-invoice-item-name {
  margin-bottom: 5px;
  font-weight: bold;
}

.account-invoice-total-title {
  font-weight: bold;
}

// Totals

.account-invoice-totals {
  .account-wrapper & {
    width: auto;
    margin-top: 0;
    margin-left: auto;
  }

  th,
  td {
    padding-right: $account-gutter-inner;

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }
}

/* scss-combine:account/pages/settings */
.account-page-settings {
  .alert:first-of-type {
    margin-top: 0;
  }

  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-settings-form {
  @extend %clearfix;
  padding-top: $account-gutter-inner;

  .form-field {
    float: left;
    width: 50%;
    padding-right: $account-gutter-inner / 2;
    vertical-align: top;

    &:nth-of-type(2n+0) {
      padding-right: 0;
      padding-left: $account-gutter-inner / 2;
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}


/* scss-combine:account/pages/auth-login */

.account-page-login .alert {
  max-width: 860px;
  margin-top: 0;
  margin-right: auto;
  margin-bottom: $account-gutter;
  margin-left: auto;
}

.account-login-wrapper {
  max-width: 860px;
  margin-right: auto;
  margin-left: auto;

  @media screen and (min-width: $account-breakpoint-small) {
    display: table;
    width: 100%;
    table-layout: fixed;
  }
}

.account-page-login .account-sub-heading {
  margin: 0 0 20px;
}

.account-login,
.account-create {
  @media screen and (min-width: $account-breakpoint-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Login Form

.account-login {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    padding: $account-gutter-inner (30px + $account-gutter-inner) $account-gutter-inner 0;
  }
}

.account-login-button {
  margin-right: $account-gutter-inner;
}

// New Account

.account-create {
  padding: $account-gutter-inner;
  background: $account-card-background-color;
}

.account-create-intro {
  &:empty {
    display: none;
  }
}

.account-create-facts {
  padding-left: 20px;
  margin-bottom: 30px;
  list-style: disc;

  li:empty {
    display: none;
  }
}

/* scss-combine:account/pages/auth-forgot */
.account-page-forgot .account-body {
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;
  float: none;
}

.account-forgot-form {
  display: table;
  width: 100%;
  margin-top: $account-gutter;

  .form-field,
  .form-actions {
    display: table-cell;
    text-align: left;
    vertical-align: bottom;
  }

  .form-actions {
    width: 1px;
    padding-left: 10px;
  }
}

/* scss-combine:account/pages/auth-create */
.account-page-create {
  .account-header {
    padding-bottom: $account-gutter-inner;
    margin-bottom: $account-gutter;
    border-bottom: 1px solid $account-border-color;
  }

  .account-body {
    max-width: 860px;
    margin-right: auto;
    margin-left: auto;
  }
}

.account-create-form {

  @media screen and (min-width: $account-breakpoint-small) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }
    }
  }
}

.account-create-details,
.account-create-address {
  @extend %clearfix;
  padding-bottom: $account-gutter-inner;
  margin-bottom: $account-gutter;
  border-bottom: 1px solid $account-border-color;
}

.account-create-form-actions {
  text-align: center;
}

.account-create-captcha {
  display: inline-block;
  vertical-align: middle;

  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    margin-right: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/auth-created */
.account-page-created .account-body {
  float: none;
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;

  p {
    margin-bottom: $account-gutter;
    line-height: 1.5;
  }

  em {
    font-family: monospace;
    font-style: normal;
    font-weight: bold;
    background: $account-card-background-color;
  }
}

/* scss-combine:account/pages/auth-new-password */
.account-page-new-password .account-content {
  width: 100%;
  max-width: 400px;
  margin-right: auto;
  margin-left: auto;
}

.new-password-requirements {
  margin-bottom: $account-gutter;
  text-align: center;
}


/* scss-combine:account/pages/gift-certificate */
// (optional) gift card nav

@if ($giftcard-nav-use-default-styles) {
  .giftcard-nav {
    padding: $account-gutter * 2;
    margin-bottom: $account-gutter * 2;
    text-align: center;
  }

  .giftcard-nav-item {
    display: inline-block;
    margin: 0 1em;
    color: $account-link-color;

    &.is-active { color: $account-text-color; }
  }
}

.giftcard-heading-container {
  margin-bottom: $account-gutter * 2;
  text-align: center;
}


// Purchase

.giftcard-form-help-message {
  display: block;
  margin-top: 6px;
}

.giftcard-form-preview {
  iframe { width: 100%; }
}

.giftcard-single-theme {
  display: none;
}


// Check balance

.giftcard-balance-container {
  max-width: 600px;
  margin: 0 auto;
}

.form-giftcard-balance {
  position: relative;

  .form-field-title {
    position: absolute;
    left: -9999px;
  }

  .form-field-control {
    display: block;

    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      margin-right: calc(30% + #{$account-gutter-inner});
    }
  }

  .form-actions {
    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      position: absolute;
      top: 0;
      right: 0;
      width: 30%;
    }
  }

  .button {
    width: 100%;
  }
}


// Redeem

.giftcard-redeem-steps {
  line-height: 1.5;

  li { margin-bottom: 0.75em; }
}


/* scss-combine:account/account */
// Account Overrides
.account-nav { text-align: left; }

.account-content {
  //scss-lint:disable ImportantRule
  padding-top: 0 !important;
  border-top: 0 !important;

  // Forms
  > .form {
    margin: 0;

    .form-label {
      width: 100%;
      font-size: $font-size;
    }
  }

  .form-actions {
    padding: 0;
    margin: ($gutter * 2) 0 0;
    border: 0;
  }

  // Forgot password form
  .account-forgot-form {
    display: block;

    .form-field {
      display: inline-block;
      width: 100%;
      text-align: center;
    }

    .form-actions {
      //scss-lint:disable SelectorDepth, NestingDepth
      display: inline-block;
      float: none;
      width: 35%;

      .button {
        display: block;
        margin: 0 auto;
      }
    }
  }

  // Recently Viewed Products
  .account-product-wrapper {
    margin-right: -20px;
    margin-left: -20px;
  }

  .product-grid-item {
    &:nth-child(4n + 1) { clear: left; }

    @include fluid-width(200, 920, 40);

    @include breakpoint(xl-only) {
      @include fluid-width(200, 680, 40);

      &:nth-child(4n + 1) { clear: none; }
      &:nth-child(3n + 1) { clear: left; }
    }

    @include breakpoint(l-only) {
      @include fluid-width(200, 440, 40);

      &:nth-child(3n + 1) { clear: none; }
      &:nth-child(2n + 1) { clear: left; }
    }

    .product-item-compare,
    .quick-shop-trigger { display: none; }
  }
}

// Layout
.account-body {
  float: left;
  margin-left: 0;
}

.account-header > .account-heading { display: none; }

// Products Grid
.price-rrp { text-decoration: line-through; }

// Login Screen
.account-login,
.account-create {
  @include breakpoint(m-only) {
    display: block;
    float: left;
    width: 100%;
    padding-left: 0;
  }

  .account-login-form .form-field:first-child { margin-top: 0; }
}

/* scss-combine:account/auth */
// Login Page
.account-page-login { text-align: left; }


// Components
/* scss-combine:components/header */
.main-header {
  position: relative;
  z-index: 15;
  -webkit-font-smoothing: antialiased;
  background-color: $header-background-color;

  @include breakpoint(l-up) { padding-top: ($gutter * 2) + 10px; }
  @include breakpoint(l-only) { z-index: 2; }

  &.header-alternate { padding-top: 0; }
}

.header-topbar {
  width: 100%;
  height: 50px;
  background-color: darken($header-background-color, 8%);

  @include breakpoint(l-only) {
    height: 45px;
    background-color: transparent;
  }

  .header-topbar-inner {
    @include outer-container;
    height: 50px;
    padding: 0 $gutter * 2;
    font-size: 0;

    @include breakpoint(l-only) { height: 45px; }
  }
}

.header-inner {
  @include outer-container;
  position: relative;
  z-index: 0;

  .logo-center & {
    font-size: 0;
    text-align: center;
  }
}

.header-branding {
  @include span-columns(4);
  padding-top: 0;
  padding-bottom: 0;
  padding-left: $gutter * 2;
  margin-top: 0;
  margin-bottom: $gutter * 2;

  @include breakpoint(l-only) {
    @include span-columns(12);
    @include omega();
    padding-top: $gutter * 2;
    padding-bottom: $gutter * 2;
    padding-left: 0;
    margin-top: 45px;
    margin-bottom: 0;
  }

  .js & {
    visibility: hidden;

    &.show-branding { visibility: visible; }
  }

  .navigation-alternate & {
    display: inline-block;
    float: none;
    width: 20%;
    padding-right: $gutter * 2;
    padding-left: 0;
    margin: ($gutter * 2) 0;
    vertical-align: middle;

    @include breakpoint(l-only) { display: none; }
  }

  .navigation-alternate.navigation-left & {
    //scss-lint:disable SelectorDepth
    padding-right: $gutter * 2;

    .header-branding-logo img { float: right; }
  }

  .navigation-alternate.navigation-center & {
    padding-right: 0;
    text-align: center;

    .header-branding-logo {
      display: inline-block;
      text-align: center;
    }
  }

  .logo-right & {
    @include omega();
    float: right;
    padding-right: $gutter * 2;
    padding-left: 0;

    .header-branding-logo {
      float: right;

      @include breakpoint(l-only) { float: none; }
    }
  }

  .logo-center & {
    display: inline-block;
    float: none;
    width: 30%;
    padding-left: 0;
    margin-right: 0;
    margin-left: 35%;
    text-align: center;

    @include breakpoint(l-only) {
      width: 100%;
      margin-left: 0;
    }
  }
}

.header-branding-logo {
  float: left;

  @include breakpoint(l-only) {
    display: block;
    float: none;
    text-align: center;
  }

  .logo-center & {
    display: inline-block;
    float: none;
  }

  .navigation-alternate & {
    width: 100%;
    text-align: left;
  }

  img {
    display: block;

    @include breakpoint(l-only) {
      display: inline-block;
      margin-bottom: -6px;
    }

    .logo-center & {
      @include breakpoint(l-only) { margin-bottom: 0; }
    }

    .logo-center.header-alternate & {
      @include breakpoint(l-only) { margin-bottom: -6px; }
    }

    .navigation-alternate & {
      display: inline-block;
      margin-bottom: -3px;
    }

    .retina-logo & {
      max-width: 200px;
      max-height: 85px;

      @include breakpoint(s-only) { max-width: 180px; }
    }
  }
}

.header-branding-text {
  margin: 0;
  font-family: $body-font;
  font-weight: 300;
  line-height: 36px;

  @include breakpoint(l-only) { text-align: center; }

  .logo-right & {
    text-align: right;

    @include breakpoint(l-only) { text-align: center; }
  }

  .navigation-left & {
    float: right;
    text-align: right;
  }

  a { color: $heading-color; }

  a:hover { text-decoration: none; }
}

.mobile-header-branding {
  display: none;
  padding: ($gutter * 2) 0;
  text-align: center;

  @include breakpoint(l-only) { display: block; }
}

.header-currency-selector {
  @include span-columns(3);
  @include omega();
  position: relative;
  right: - $navigation-padding;

  @include breakpoint(l-only) { display: none; }

  .header-alternate & {
    right: auto;
    display: inline-block;
    float: left;
    width: 20%;
    height: 50px;
    text-align: left;
    vertical-align: middle;

    @include breakpoint(l-only) { display: none; }
  }

  .form-select-wrapper {
    display: inline-block;
    float: right;
    width: auto;
    min-width: 0;
    padding: 0 20px 0 0;
    color: $navigation-color;
    text-align: right;
    background-color: transparent;
    border-color: transparent;
    transition: color 0.2s $ease-out-quad;

    &:hover { color: $navigation-hover-color; }

    .header-alternate & {
      float: left;
      height: 50px;
      font-size: $meta-size;
      font-weight: bold;
      line-height: 48px;
      letter-spacing: 0.5px;
      text-align: left;
    }

    &::after {
      //scss-lint:disable NestingDepth
      right: 2px;
      border-color: $navigation-color;

      .header-alternate & {
        top: 48%;
        right: 0;
        float: left;
        margin-left: 10px;
        border-color: mix(darken($header-background-color, 10%), $navigation-color, 50%);
      }
    }
  }
}

/* scss-combine:components/header-tools */
//scss-lint:disable NestingDepth
.header-tools {
  @include span-columns(8);
  @include omega();
  position: relative;
  height: auto;
  padding-right: $gutter * 2;

  @include breakpoint(l-only) {
    @include span-columns(12);
    position: absolute;
    top: 0;
    left: 0;
    height: 45px;
    line-height: 45px;
    border-bottom: 1px solid darken($header-background-color, 18%);
  }

  .header-alternate & {
    display: inline-block;
    float: none;
    width: 80%;
    padding: 0;
    text-align: right;
    vertical-align: middle;

    @include breakpoint(l-only) {
      width: 100%;
      text-align: left;
    }
  }

  .logo-right & {
    float: left;
    padding-right: 0;
    padding-left: $gutter * 2;

    @include breakpoint(l-only) { padding-left: 0; }

    .header-tools-inner { float: left; }
  }

  .header-alternate.logo-right & {
    .header-tools-inner {
      float: right;

      @include breakpoint(l-only) { float: left; }
    }
  }

  .logo-center & {
    display: inline-block;
    float: none;
    width: 35%;
    padding-right: 0;
    font-size: $font-size;
    vertical-align: top;

    @include breakpoint(l-only) { width: 100%; }
  }

  .header-alternate.logo-center & {
    width: 80%;

    @include breakpoint(l-only) { width: 100%; }
  }

  .cart-count {
    position: absolute;
    top: 3px;
    left: 13px;
    display: block;
    width: 14px;
    height: 14px;
    font-size: 10px;
    font-weight: 400;
    line-height: 14px;
    color: $light-color;
    text-align: center;
    background-color: $accent-color;
    border-radius: 50%;

    .header-alternate & {
      position: absolute;
      top: 50%;
      left: 0;
      width: 16px;
      height: 16px;
      margin-top: -8px;
      font-size: 11px;
      line-height: 16px;
      color: $accent-color;
      background-color: $light-color;
    }
  }
}

.header-tools-inner {
  @include breakpoint(l-up) { float: right; }

  .header-alternate & {
    display: inline-block;
    float: none;
  }

  .logo-center & { padding-right: $gutter * 2; }

  .header-alternate.logo-center & { padding-right: 0; }
}

.header-tools-item {
  position: relative;
  z-index: inherit;
  float: left;
  height: 30px;
  line-height: 30px;
  border: 1px solid $header-background-color;
  border-left-color: darken($header-background-color, 18%);

  @include breakpoint(l-only) {
    display: none;

    &.navigation-toggle-mobile-container { display: block; }
  }

  .header-alternate & {
    height: 50px;
    font-size: $meta-size;
    line-height: 50px;
    border: 0;
  }

  &.cart-toggle {

    &.has-cart-items {
      .header-alternate & { margin-left: 13px; }
    }

    &.visible {
      position: relative;
      top: -10px;
      height: 50px;
      line-height: 50px;

      .header-alternate & {
        top: auto;
        height: 50px;
        line-height: 50px;
        background-color: transparent;
        border: 0;
      }

      .cart-count {
        top: 13px;

        .header-alternate & { top: 50%; }
      }
    }
  }

  &.visible {
    background-color: $body-background-color;
    border: 1px solid $border-color;
    border-bottom-color: $body-background-color;

    // scss-lint:disable UnnecessaryParentReference
    & + li {
      z-index: -1;

      .header-alternate & { z-index: 1; }
    }
  }

  // skipping the mobile nav toggle
  &:nth-child(2) { border: 0; }

  &:last-child {
    margin-left: -1px;

    a { padding-right: 0; }
	}

  &.navigation-toggle-mobile-container {
    float: left;
    border: 0;

    @include breakpoint(l-up) { display: none; }
  }

  &:focus {
    border: 0;
    outline: 0;
  }

  .search-form-wrapper {
    display: none;
    margin-right: 20px;

    &.visible {
      display: inline-block;
      float: right;
      vertical-align: middle;
    }

    .header-alternate & {
      width: 200px;
      font-size: 14px;
    }
  }

  .search-input {
    position: relative;
    top: -7px;
    width: 100%;
    height: 45px;
    padding: 6px 10px;
    line-height: 45px;
    background-color: transparent;
    border: 1px solid $border-color;
    border-radius: 3px;
    box-sizing: border-box;

    .header-alternate & {
      top: 1px;
      height: 35px;
      line-height: 35px;
      border-color: darken($header-background-color, 18%);
    }

    &::placeholder { color: $navigation-color; }

    &:focus {
      color: $navigation-color;
      border-color: darken($header-background-color, 18%);
      outline: none;
    }
  }

  .search-submit {
    @extend %button-reset;
    position: absolute;
    top: 2px;
    right: 35px;
    color: $navigation-color;
  }

  > a {
    position: relative;
    padding: 10px 20px;

    .label {
      margin-left: 12px;
      font-size: $meta-size;
      font-weight: bold;
      letter-spacing: 0.5px;
      color: $navigation-color;
      text-transform: uppercase;
      transition: color 0.2s $ease-out-quad;
    }

    &:hover .label { color: $navigation-hover-color; }

    .hidden { display: none; }

    [class^="icon"] {
      //scss-lint:disable SelectorDepth
      position: relative;
      top: 2px;
      width: 12px;
      height: 12px;
      color: $navigation-color;

      .header-alternate & { color: mix(darken($header-background-color, 10%), $navigation-color, 50%); }
  	}
	}
}

.search-toggle > a {
  .main-header:not(.header-alternate) & { padding-left: 0; }
}

/* scss-combine:components/cart/cart-preview */
.cart-preview-wrap {
  position: absolute;
  top: 48px;
  right: -1px;
  z-index: -1;
  display: none;
  width: 360px;
  text-align: left;
  background-color: $body-background-color;
  border: 1px solid $border-color;

  &.visible { display: block; }

  .header-alternate & {
    top: 50px;
    z-index: 1;
    border-top: 0;
  }
}

.cart-preview-title {
  padding: 20px;
  margin-bottom: 20px;
  border-bottom: 1px solid $border-color;
}

.cart-preview-body { padding: 20px; }

.cart-preview-list {
  height: auto;
  max-height: 250px;
  overflow-y: scroll;
}

.cart-preview-list-item {
  display: block;
  float: left;
  width: 100%;
  margin-bottom: 15px;

  &:last-child { margin-bottom: 0; }
}

.cart-preview-list-item-image-wrap { @include span-columns(3); }

.cart-preview-list-item-image {
  @include maintain-ratio(1 1);
  position: relative;
  max-width: 100%;
  overflow: hidden;
  border: 1px solid darken($body-background-color, 15%);

  a {
    @include coverer;
    float: left;
    width: 100%;
    margin: 0 auto;
    overflow: hidden;

    img {
      @include centerer;
      position: relative;
      display: block;
      height: 100%;
      max-width: none;
    }
  }
}

.cart-preview-list-item-details {
  @include span-columns(9);
  padding-left: 10px;
}

.cart-preview-list-item-name {
  margin-bottom: 5px;
  font-size: 15px;
  line-height: 1.5em;
}

.cart-preview-list-item-price {
  font-size: 15px;
  line-height: 15px;
}

.cart-preview-list-item-price-quantity {
  position: relative;
  top: -1px;
  display: inline-block;
  padding: 4px 6px;
  margin-left: 5px;
  font-size: 11px;
  line-height: 10px;
  color: $meta-color;
  text-align: center;
  border: 1px solid darken($body-background-color, 15%);
  border-radius: 4px;
}

.cart-preview-subtotal {
  padding: 20px;
  padding-bottom: 23px;
  font-size: $font-size;
  line-height: $font-size;
  border-top: 1px solid darken($body-background-color, 15%);

  .label,
  .total {
    display: inline-block;
    vertical-align: middle;
  }

  .label::after { content: ":"; }

  .total {
    float: right;
    font-weight: bold;
  }
}

.cart-preview-footer {
  float: left;
  width: 100%;
  padding: 20px;
  text-align: left;
  background-color: darken($body-background-color, 2%);
  border-top: 1px solid darken($body-background-color, 15%);

  .button {
    float: left;
    width: 48%;
    margin-left: 0;

    &:first-of-type { margin-right: 2%; }
    &:last-of-type { margin-left: 2%; }
  }
}

.cart-preview-empty {
  padding: 40px;
  font-size: $font-size;
  line-height: $font-size;
  text-align: center;
}

/* scss-combine:components/cart/apple-pay */
.apple-pay-checkout-button {
  display: none;
  width: 100%;
  height: 40px;
  margin-top: $gutter;
  cursor: pointer;
  background: {
    position: 50% 50%;
    repeat: no-repeat;
    size: 100% 60%;
  }
  border-radius: 5px;
  box-sizing: border-box;

  //scss-lint:disable ColorVariable, ColorKeyword, NameFormat
  @if stencilString("style_apple_pay_button") == "white" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
  } @else if stencilString("style_apple_pay_button") == "white-border" {
    background-color: white;
    background-image: -webkit-named-image(apple-pay-logo-black);
    border: 0.5px solid black;
  } @else {
    background-color: black;
    background-image: -webkit-named-image(apple-pay-logo-white);
  }

  .apple-pay-supported & { display: block; }
}

/* scss-combine:components/sub-header */
.sub-header {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;
  overflow: hidden;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
    text-align: center;
  }
}

.breadcrumb-container {
  @include span-columns(12);
  padding-top: $gutter * 2;
}

.breadcrumb-item {
  display: inline-block;
  font-size: $navigation-size;
  color: $accent-color;

  &:after {
    display: inline-block;
    padding-right: 8px;
    padding-left: 6px;
    color: $border-color;
    content: "/";
  }

  &:last-child:after {
    content: none;
  }
}

/* scss-combine:components/footer */
.main-footer {
  padding-top: 50px;
  padding-bottom: 50px;
  -webkit-font-smoothing: antialiased;
  background-color: $footer-background-color;
  border-top: 1px solid $border-color;

  @include breakpoint(l-only) { padding-bottom: 0; }

  .container {
    @include outer-container;
    padding-right: $gutter * 2;
    padding-left: $gutter * 2;

    @include breakpoint(l-only) {
      padding-right: 0;
      padding-left: 0;
    }
  }
}

.footer-left {
  @include span-columns(8);

  @include breakpoint(l-only) {
    @include span-columns(12);
    margin-bottom: 10px;
  }
}

.footer-right {
  @include span-columns(4);

  @include breakpoint(l-only) {
    @include span-columns(12);
  }
}

.footer-navigation {
  @include span-columns(3);
  float: left;

  @include breakpoint(xl-only) {
    @include span-columns(4);
  }

  @include breakpoint(l-only) {
    width: 100%;
    margin-right: 0;
    margin-bottom: 50px;
    text-align: center;
  }
}

.footer-navigation-list-item {
  //scss-lint:disable VariableForProperty
  > a {
    display: block;
    padding: 12px 0;
    font-family: $navigation-font;
    font-size: $navigation-size;
    line-height: $navigation-size;
    color: rgba($footer-color, 0.85);
    text-transform: capitalize;

    &:hover { color: $footer-color; }

    [class^="icon"] { padding-right: 6px; }
  }

  &:last-child a { padding-bottom: 0; }
}

.footer-description {
  @include span-columns(6);
  display: block;
  float: left;
  padding-right: 15%;

  @include breakpoint(xl-only) {
    @include span-columns(4);
    padding-right: 0;
  }

  @include breakpoint(l-only) {
    width: 100%;
    padding-right: 0;
    text-align: center;

    p {
      padding-right: 10%;
      padding-left: 10%;
      margin-top: 10px;
    }
  }

  p { color: $footer-color; }
}

.footer-currency-selector {
  display: none;
  float: left;
  width: 100%;
  text-align: center;

  @include breakpoint(l-only) { display: block; }

  .footer-currency-selector-title { color: $footer-color; }

  .form-select-wrapper {
    display: inline-block;
    width: auto;
    min-width: 0;
    padding-right: 40px;
    color: $footer-color;
    background-color: $footer-background-color;

    &::after { border-color: $footer-color; }
  }
}

.footer-newsletter {
  width: 100%;

  @include breakpoint(l-only) {
    padding-top: ($gutter * 4);
    padding-bottom: ($gutter * 5);
    margin-top: 30px;
    text-align: center;
    background-color: darken($accent-color, 3%);

    .footer-section-title { color: $light-color; }
  }
}

.footer-newsletter-form {
  @include breakpoint(l-only) {
    padding-right: 10%;
    padding-left: 10%;
  }

  .newsletter-signup-email {
    display: inline-block;
    width: 73%;
    min-width: 0;
    margin-right: 2%;

    @include breakpoint(m-only) { width: 63%;}
  }

  .newsletter-signup-submit {
    //scss-lint:disable NestingDepth
    display: inline-block;
    width: 25%;

    @include breakpoint(m-only) { width: 35%;}

    input {
      height: 43px;

      @if (lightness($footer-background-color) > 50) {
        background-color: $primary-button-background-color;
        border-color: $primary-button-background-color;

        &:hover {
          background-color: $primary-button-hover;
          border-color: $primary-button-hover;
        }
      } @else {
        background-color: mix($footer-background-color, $footer-color, 80%);
        border-color: mix($footer-background-color, $footer-color, 80%);

        &:hover {
          background-color: mix($footer-background-color, $footer-color, 90%);
          border-color: mix($footer-background-color, $footer-color, 90%);
        }
      }
    }
  }
}

.footer-section-title {
  //scss-lint:disable NameFormat
  margin-top: 0;
  font-size: 16px;
  line-height: 16px;
  color: $footer-color;

  @if (stencilFontFamily("heading-font") == "Lora") {
    font-weight: 600;
    text-transform: capitalize;
  }
}

.footer-payment-icons {
  float: left;
  width: 100%;
  margin-top: $gutter * 2;
  text-align: right;

  @include breakpoint(l-only) {
    padding-bottom: 55px;
    margin-top: 0;
    text-align: center;
    background-color: darken($accent-color, 3%);
  }
}

.footer-payment-icons-list {
  float: left;
  width: 100%;

  li {
    display: inline-block;
    margin-right: 10px;
    font-size: 25px;
    color: rgba($footer-color, 0.4);

    &:last-of-type { margin-right: 0; }
  }
}

// Footer Credits
.footer-attribution,
.footer-copyright {
  float: left;
  width: 100%;
  margin-top: 5px;
  color: rgba($footer-color, 0.4);
  text-align: right;

  @include breakpoint(l-only) {
    padding: 0 10%;
    margin-top: 0;
    text-align: center;
    background-color: darken($accent-color, 3%);
  }

  a {
    color: rgba($footer-color, 0.4);
    border-bottom: 1px solid rgba($footer-color, 0.2);

    &:hover { color: $footer-color; }
  }
}

.footer-copyright {
  margin: 0;

  @include breakpoint(l-only) { padding-bottom: $gutter * 2; }

  p { margin: 0; }
}

/* scss-combine:components/banners */
$page-banner-border: darken($banner-background-color, 18%);

.page-banner {
  letter-spacing: 1px;
  color: $light-color;
  background-color: $banner-background-color;
}

.page-banner-inner {
  padding: ($gutter * 2) 0;
  text-align: center;
  border-top: 1px solid $page-banner-border;
  border-right: 0;
  border-left: 0;

  &:first-of-type {
    border-top: 0;

    .page-banner-bottom & { border-top: 1px solid $page-banner-border; }
  }

  &:last-of-type {
    border-bottom: 1px solid $page-banner-border;

    .page-banner-bottom & { border-bottom: 0; }
  }

  > .banner {
    @include outer-container();

    .full-width-banners & {
      max-width: 100%;
      padding: 0 ($gutter * 2);
    }
  }

  .rte img {
    display: block;
    max-width: 100%;
    height: auto;
  }

  p {
    &:first-of-type { margin-top: 0; }
    &:last-of-type { margin-bottom: 0; }
  }
}

.shipping-banner {
  color: $light-color;
  background-color: rgba($success-color, 0.6);

  .page-banner-inner {
    border-top: 1px solid $success-color;

    &:last-of-type { border-bottom: 1px solid $success-color; }
  }
}

/* scss-combine:components/alerts */
// Alert Messages
.alert {
  position: relative;
  letter-spacing: 1px;
  color: $light-color;
  transition: opacity 0.3s ease;

  @include breakpoint(m-only) { text-align: center; }

  &.dismissed { opacity: 0; }

  &.hidden { display: none; }

  .narrow & { text-align: center; }

  .alert-dismiss {
    position: absolute;
    top: 10px;
    right: 10px;
    color: inherit;
    cursor: pointer;
    opacity: 0.8;

    &:hover { opacity: 1; }

    svg {
      display: block;
      width: 13px;
      height: 13px;
      fill: $light-color;
    }
  }

  &.alert-success,
  &.alert-error,
  &.alert-info {
    padding: 15px 20px;
    margin: $gutter 0;
    border: 1px solid $border-color;

    .quick-shop & { margin: 0 0 20px; }
  }

  &.alert-success {
    background-color: rgba($success-color, 0.6);
    border-color: $success-color;
  }

  &.alert-error {
    background-color: rgba($error-color, 0.6);
    border-color: $error-color;
  }

  &.alert-info {
    background-color: rgba($accent-color, 0.6);
    border-color: $accent-color;
  }

  a {
    color: $light-color;
    text-decoration: underline;
  }
}

/* scss-combine:components/faceted-search */
.faceted-search {
  padding-right: 30px;

  @include breakpoint(l-only) {
    display: none;
    padding-right: 0;
    text-align: center;

    &.visible {
      display: block;
    }
  }
}

.faceted-sidebar-toggle {
  display: none;
  margin-bottom: 50px;

  @include breakpoint(l-only) {
    display: inline-block;
  }
}

.faceted-search-title {
  margin-top: 0;
  margin-bottom: 5px;
  font-size: $font-size;
}

.faceted-search-filter {
  margin-bottom: $gutter * 3;
}

.faceted-search-item {

  @include breakpoint(l-only) {
    max-width: 280px;
    margin: 0 auto;
    border-top: 1px solid rgba($border-color, 0.5);

    &:first-of-type {
      border-top: 0;
    }
  }

  a {
    position: relative;
    display: inline-block;
    vertical-align: middle;

    @include breakpoint(l-only) {
      width: 100%;
    }
  }

  .facet-checkbox {
    display: inline-block;
    margin-top: 2px;
    margin-right: 10px;
    vertical-align: middle;

    @include breakpoint(l-only) {
      position: absolute;
      top: 10px;
      left: 10px;
    }
  }

  .faceted-search-item-label {
    position: relative;
    display: inline-block;
    max-width: 100%;
    padding-right: 50px;
    line-height: inherit;
    vertical-align: middle;

    @include breakpoint(l-only) {
      position: static;
      max-width: 240px;
      padding: 5px 0;
    }
  }

  // scss-lint:disable VariableForProperty
  .faceted-search-item-count {
    position: absolute;
    right: 0;
    bottom: 0;
    width: 40px;
    color: rgba($dark-accent-color,0.5);

    @include breakpoint(l-only) {
      position: absolute;
      top: 10px;
      right: 7px;
      width: auto;
      font-size: 14px;
      line-height: 1;
    }
  }
}

// price range
.facet-range-input-group {
  @extend %clearfix;
  position: relative;
  padding-top: 10px;
  padding-right: 80px;

  @include breakpoint(l-only) {
    max-width: 290px;
    margin: 0 auto;
  }
}

.facet-range-inputs {
  .form-field {
    float: left;
    width: 48%;

    &:last-child {
      float: right;
    }

    .form-input {
      width: 100%;
      height: 41px;
      min-height: 0;

      // scss-lint:disable NestingDepth
      &:focus {
        border-color: $accent-color;
        outline: none;
      }
    }
  }
}

.facet-submit {
  position: absolute;
  top: 10px;
  right: 0;
  width: 70px;
  height: 40px;
  padding-right: 0;
  padding-left: 0;
  font-family: $body-font;
  font-size: 13px;
  letter-spacing: 0;
  text-transform: none;
}


// star rating
.facet-rating {
  .star {
    font-size: 12px;
    color: $border-color;

    &.checked {
      color: $accent-color;
    }
  }
}

.facet-selected-filters {
  padding-bottom: 25px;
  margin-bottom: 30px;
  border-bottom: 1px solid $border-color;

  .clear-all {
    display: inline-block;
    width: 100%;
    padding: 2px;
    clear: both;
    font-size: 14px;
    color: $accent-color;
  }
}

.selected-filter-items {
  float: left;
  width: 100%;
  margin-bottom: 18px;
}

.selected-filter-item {
  @extend %ellipsis;
  position: relative;
  display: inline-block;
  float: left;
  padding: 10px 15px;
  margin: 0 7px 7px 0;
  font-size: 13px;
  color: $body-color;
  background-color: darken(mix($body-background-color, $border-color), 2%);
  border-radius: 4px;
  transition: all 0.2s $ease-out-quad;

  .remove {
    position: relative;
    top: 1px;
    padding-left: 7px;
    font-size: 17px;
    line-height: 17px;
    color: $dark-color;
    transition: all 0.2s $ease-out-quad;
  }

  &:hover {
    color: $light-color;
    background-color: $accent-color;

    .remove {
      color: $light-color;
    }
  }
}

/* scss-combine:components/sorting */
// Facet Sorting
.sorting {
  padding-right: 30px;

  @include breakpoint(l-only) { padding-right: 0; }

  .form-label { @extend %show-for-sr; }
}

.sorting-title {
  margin-top: 0;
  margin-bottom: 5px;
  font-size: $font-size;
}

.product-sort-select { margin-bottom: $gutter * 3; }

// Sorting
.product-grid-sorting {
  position: relative;
  margin-bottom: $gutter * 3;

  @include breakpoint(m-only) { margin-bottom: 20px; }

  &:before {
    position: absolute;
    top: 50%;
    display: block;
    width: 100%;
    height: 1px;
    border-bottom: 1px solid $border-color;
    content: "";
  }

  .sorting {
    padding-right: 0;
    margin-top: -$gutter * 2;
    text-align: center;
  }

  .product-sort-select {
    position: relative;
    z-index: 1;
    display: inline-block;
    width: 33.333%;
    padding: $gutter * 2;
    margin-bottom: 0;
    background-color: $body-background-color;

    @include breakpoint(l-only) { width: 50%; }

    @include breakpoint(m-only) {
      width: 100%;
      padding: 0;
    }
  }

  .form-select-wrapper {
    height: 60px;
    line-height: 58px;
    background-color: $body-background-color;
    border-color: $border-color;
  }

  .sorting-title { @extend .show-for-sr; }
}


// Navigation
/* scss-combine:components/navigation/nav-common */
.navigation-wrap {
  float: left;
  width: 100%;
  font-family: $navigation-font;
  background-color: $header-background-color;
}

.navigation {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }

  a {
    display: block;
    font-size: $navigation-size;
    line-height: $navigation-size;
    color: $navigation-color;
  }
}

.nav-menu {
  @include span-columns(9, block-collapse);
  padding: 0;
  list-style-type: none;

  @include breakpoint(l-only) {
    //scss-lint:disable ImportantRule - overwriting the Bourbon block-collapse
    width: 100% !important;
  }

  .menu-open & { display: block; }
}

.nav-menu-item {
  position: relative;
  float: left;
  width: 100%;
  border: 1px solid $header-background-color;
  transition: color 0.2s $ease-out-quad;

  > a {
    position: relative;
    padding: $navigation-padding;
    border-bottom: 1px solid transparent;
  }

  > a:hover { color: $navigation-hover-color; }

  > .nav-icon { display: none; }
}

// scss-lint:disable MergeableSelector
.nav-menu-item.has-dropdown {
  position: relative;

  > a {
    padding-right: 40px;

    &::after {
      position: absolute;
      top: 50%;
      right: 0;
      width: 0;
      height: 0;
      margin-top: -2px;
      margin-right: 15px;
      border-top: 5px solid $navigation-color;
      border-right: 5px solid transparent;
      border-left: 5px solid transparent;
      content: "";
      transition: border-color 0.2s $ease-out-quad;
    }

    &:hover::after { border-top-color: $navigation-hover-color; }
  }

  .mobile & > ul { display: none; }
  .mobile &.menu-open > ul { display: block; }
}

// scss-lint:disable MergeableSelector
.nav-menu-item.menu-open {
  z-index: 15;
  border-color: darken($header-background-color, 18%);

  > a {
    position: relative;
    z-index: 2;
    color: $navigation-hover-color;
    background-color: $header-background-color;
    border-bottom: 1px solid $header-background-color;

    &::after { border-top-color: $navigation-hover-color; }
  }
}

.nav-submenu {
  @include opacity(0);
  @include transition(all, 0.1s, ease-in-out);
  position: relative;
  top: 100%;
  display: none;
  padding-top: $navigation-padding;
  padding-bottom: $navigation-padding;
  background-color: $header-background-color;
  visibility: hidden;

  &.visible {
    @include opacity(1);
    display: block;
    visibility: visible;
  }

  a {
    display: block;
    padding: 10px $navigation-padding;

    .has-dropdown & {
      padding-right: $navigation-padding + 20;
    }
  }
}

.nav-submenu-item.has-dropdown {
  > a { position: relative; }

  &.menu-open {
    > a::after {
      border-left-color: $accent-color;
      transform: rotate(90deg);
    }
  }

  &:first-child {
    > a::after { margin-top: -5px; }
  }
}

.navigation-tier-tertiary {
  @include transition(all, 0.1s, ease-in-out);
  position: relative;
  height: 0;
  padding-top: 0;
  padding-bottom: 0;
  margin-top: 0;
  margin-bottom: 0;
  overflow: hidden;
  background-color: darken($header-background-color, 5%);
  transition: all 0.4s $ease-out-quad;

  .menu-open > & {
    height: auto;
    padding-top: 10px;
    padding-bottom: 10px;
    margin-top: 10px;
    margin-bottom: 10px;
  }
}

.nav-icon {
  position: absolute;
  top: 8px;
  right: $navigation-padding;
  left: auto;
  width: rem-calc(20);
  height: rem-calc(20);
  margin-top: rem-calc(0);
  font-size: rem-calc(8);
  text-align: center;
  cursor: pointer;

  @include breakpoint(l-only) {
    right: $gutter + 10;
    color: contrast($header-background-color, 35%);
  }

  &::before {
    display: block;
    margin-top: 7px;
  }

  &:hover { color: $navigation-hover-color; }
}

.js .nav-menu {
  visibility: hidden;

  &.show-menu { visibility: visible; }
}

/* scss-combine:components/navigation/nav-mobile */
@include breakpoint(l-only) {
  .navigation-wrap {
    position: fixed;
    top: 0;
    z-index: 10;
    height: 100vh;
    padding: 0;
    overflow: hidden;
    background-color: rgba($dark-color, 0.7);
    opacity: 0;
    transition: all 0.4s $ease-out-quad;

    @include breakpoint(l-up) {
      background: transparent;
      opacity: 0;
    }

    .mobile-nav-open & { opacity: 1; }

    .mobile-nav-closed & {
      z-index: -2;
      background: transparent;
    }
  }

  .navigation {
    position: relative;
    top: 0;
    left: 0;
    z-index: -1;
    width: 450px;
    height: 100vh;
    padding: 0;
    margin: 0;
    overflow: hidden;
    background-color: $header-background-color;
    transform: translateX(-450px);
    transition: all 0.2s $ease-out-quad;

    > .navigation-toggle-mobile-wrapper {
      width: 100%;
      padding-top: 12px;
      line-height: 20px;
      text-align: right;
    }

    .mobile-nav-open & {
      z-index: 2;
      overflow-y: scroll;
      transform: translateX(0);
    }
  }

  .mobile-actions {
    @include outer-container;
    padding-right: 30px;
    padding-left: 30px;
    border-bottom: 1px solid darken($header-background-color, 18%);

    .mobile-action-item {
      @include span-columns(4, block-collapse);
      padding-top: 20px;
      padding-bottom: 20px;
      text-align: center;
      border-left: 1px solid darken($header-background-color, 18%);

      &:first-child { border: 0; }

      a { line-height: 24px; }
    }

    .cart-count-preview {
      > a,
      .cart-preview { display: inline-block; }

      .cart-count {
        position: relative;
        top: -3px;
        display: inline-block;
        width: 16px;
        height: 16px;
        margin-left: 3px;
        font-size: 11px;
        font-weight: 400;
        line-height: 16px;
        color: $light-color;
        text-align: center;
        background-color: $accent-color;
        border-radius: 50%;
      }
    }
  }

  .nav-menu {
    position: absolute;
    top: 50;
    z-index: 1;
    width: 100%;
    height: 100%;
    max-width: 450px;
    padding: 0;
  }

  .nav-menu-item {
    border: 0;

    &.menu-open {
      margin-bottom: -1px;
      background: contrast($header-background-color, 3%);
      border: 0;

      > a {
        margin-bottom: 20px;
        color: $navigation-color;
        background-color: transparent;
        border: 0;
        border-top: 1px solid transparent;
        border-bottom: 1px solid contrast($header-background-color, 25%);

        // scss-lint:disable NestingDepth, ImportantRule
        &::after { border-top-color: contrast($header-background-color, 35%) !important; }
      }

      > .nav-icon { display: none; }
    }

    &:first-child a { border-top: 0; }

    > a {
      padding-top: 30px;
      padding-right: 20px;
      padding-bottom: 30px;
      padding-left: 20px;
      margin-right: 30px;
      margin-left: 30px;
      border-top: 1px solid darken($header-background-color, 18%);
      transition: none;

      @include breakpoint(m-only) {
        padding-top: 20px;
        padding-bottom: 20px;
      }
    }
  }

  .nav-submenu {
    padding-top: 0;
    padding-bottom: 30px;
    background: transparent;

    @include breakpoint(m-only) { padding-bottom: 20px; }
  }

  .nav-submenu-item .nav-submenu-item { padding-left: 10px; }

  .nav-submenu-item > a {
    padding-top: 5px;
    padding-right: 20px;
    padding-bottom: 5px;
    padding-left: 20px;
    margin-right: 30px;
    margin-left: 30px;
    color: $navigation-color;
  }

  .nav-submenu-item.has-dropdown {
    position: relative;

    > a::after {
      right: auto;
      left: auto;
      margin-top: -5px;
      margin-left: 15px;
      border-left-color: $navigation-color;
    }

    &.menu-open {
      > a::after { border-left-color: $navigation-hover-color; }
    }

    .nav-icon {
      top: 4px;
      right: $navigation-padding + 25;
    }
  }

  .navigation-tier-tertiary {
    padding-top: 0;
    padding-bottom: 0;
    margin-top: 0;
    margin-bottom: 0;
    background-color: $navigation-hover-color;
    transition: none;

    .menu-open > & {
      padding-top: 20px;
      padding-bottom: 20px;
      margin-top: 20px;
      margin-bottom: 20px;
    }
  }

  .navigation-tier-tertiary-item {
    > a {
      padding-right: 20px;
      padding-left: 20px;
      margin-right: 30px;
      margin-left: 30px;
      color: $light-color;
    }
  }

  .mobile-branding {
    width: 100%;
    padding-top: 0;
    padding-right: 50px;
    padding-bottom: 30px;
    padding-left: 50px;
    text-align: center;
    border-bottom: 1px solid darken($header-background-color, 18%);

    > .header-branding-logo {
      max-width: 100%;
      text-align: center;

      img { display: inline-block; }
    }

    > .header-branding-text a {
      font-size: 25px;
      line-height: 25px;
    }
  }

  .search-form-wrapper {
    display: none;
    padding: 10px 40px;
    background-color: darken($header-background-color, 18%);
    border-bottom: 1px darken($header-background-color, 18%);

    &.visible { display: block; }

    .search-input {
      width: 100%;
      padding-top: 10px;
      padding-right: 20px;
      padding-bottom: 10px;
      padding-left: 20px;
      border: 1px solid $border-color;
      border-radius: 4px;
      box-sizing: border-box;

      &:focus {
        border: 1px solid $navigation-hover-color;
        outline: none;
      }
    }
  }

  .navigation-toggle-mobile-wrapper {
    position: relative;
    display: inline-block;
    padding-top: 0;
    padding-right: 20px;
    padding-bottom: 0;
    padding-left: 20px;
    font-size: 25px;
    line-height: 45px;
    vertical-align: top;
    background-color: transparent;
    border: 0;

    &.close { font-size: 20px; }

    > span {
      position: relative;
      top: 1px;
      color: $navigation-color;
    }
  }

  .main-header,
  .main-content,
  .main-footer,
  .page-banner {
    transform: translateX(0);
    transition: all 0.2s $ease-out-quad;
  }

  .mobile-nav-open {
    .main-header,
    .main-content,
    .main-footer,
    .page-banner { transform: translateX(450px); }
  }
}

@include breakpoint(m-only) {
  .navigation {
    width: 300px;
    transform: translateX(-300px);
  }

  .nav-menu { max-width: 300px; }

  .mobile-nav-open {
    .main-header,
    .main-content,
    .main-footer,
    .page-banner { transform: translateX(300px); }
  }
}

/* scss-combine:components/navigation/nav-desktop */
@include breakpoint(l-up) {

  .navigation-wrap {
    position: relative;
    float: none;
    height: auto;
    padding-bottom: ($gutter * 2) - $navigation-padding + 10px;
    overflow: visible;
    border-bottom: 1px solid darken($header-background-color, 18%);

    &.navigation-alternate { padding-bottom: 0; }
  }

  .navigation {
    position: relative;

    .navigation-alternate & { font-size: 0; }
    .navigation-alternate.navigation-left & { padding: 0 0 0 15px; }

    .navigation-alternate.navigation-center & {
      padding: 0;
      text-align: center;
    }
  }

  .mobile-actions,
  .navigation-toggle-mobile-wrapper,
  .mobile-search-wrapper,
  .mobile-branding,
  .navigation .mobile-list-item { display: none; }

  .nav-menu {
    margin-left: -$navigation-padding;

    .navigation-alternate & {
      position: relative;
      right: -$navigation-padding;
      display: inline-block;
      float: none;
      width: 80%;
      margin-left: 0;
      text-align: right;
      vertical-align: middle;
    }

    .navigation-center & {
      right: auto;
      width: 100%;
      text-align: center;
    }

    .navigation-left.navigation-alternate & {
      right: 0;
      text-align: left;
    }

    > li {
      width: auto;
      margin-right: 10px;

      .navigation-alternate & {
        //scss-lint:disable NestingDepth
        display: inline-block;
        float: none;

        &.mobile-list-item { display: none; }
        &:nth-last-child(2) { margin-right: 0; }
      }
    }
  }

  .nav-submenu {
    position: absolute;
    min-width: calc(180px + #{$navigation-size * 3} + #{$navigation-padding});
    margin: -1px 0 0 -1px;
    border: 1px solid darken($header-background-color, 18%);

    .navigation-alternate & { text-align: left; }

    > li > a:hover { color: $navigation-hover-color; }

    //tertiary
    .nav-submenu {
      position: absolute;
      top: 0;

      &.fly-right {
        left: 239px;
      }

      &.fly-left {
        right: 239px;
      }
    }
  }

  .nav-submenu-item {
    position: relative;
  }

  .nav-submenu-item.menu-open {
    > a { color: $navigation-hover-color; }
  }
}


