components/body-classes:
template-{{page_type}}

{{#if carousel.slides.length}}
  has-carousel
{{/if}}

{{#if theme_settings.carousel-full-width}}
  carousel-full-width
{{/if}}

{{#if banners.top}}
  has-header-banner
{{/if}}

{{#if theme_settings.retina-logo}}
  retina-logo
{{/if}}

{{#if theme_settings.banners_full_width}}
  full-width-banners
{{/if}}

{{#unless in_production}}
  dev-environment
{{/unless}}

components/brand/filters:
{{#if brand.faceted_search_enabled}}

  <a href="#" class="button button-alt faceted-sidebar-toggle">{{lang 'search.faceted.toggle_filters'}}</a>

  {{#if brand.selected.items.length}}
    {{> components/faceted-search/facet-selected brand.selected}}
  {{/if }}

  {{> components/faceted-search/facet-inputs facets=brand.facets}}
{{/if }}

components/brand/product-index:
{{> components/products/grid
  products=brand.products
  rowcount=theme_settings.products_per_row
  show_compare=brand.show_compare
  quickshop=theme_settings.show_quick_shop
  crop=theme_settings.grid_aspect_ratio}}

{{#if pagination.brand.links.length}}
  {{> components/common/pagination pagination.brand}}
{{/if}}

<div class="progress-overlay">
  <div class="inner">
    {{> components/common/spinner}}
  </div>
</div>

components/common/facebook-like:
{{#if settings.add_this}}
  {{#each settings.add_this.buttons}}
    {{#if service '===' 'facebook_like'}}
      <div id="fb-root"></div>
      <script>(function(d, s, id) {
        var js, fjs = d.getElementsByTagName(s)[0];
        if (d.getElementById(id)) return;
        js = d.createElement(s); js.id = id;
        js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.5";
        fjs.parentNode.insertBefore(js, fjs);
      }(document, 'script', 'facebook-jssdk'));</script>
    {{/if}}
  {{/each}}
{{/if}}

components/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
  <div class="currency-selector-wrapper">
    <span class="show-for-sr">{{currency_selector.active_currency_name}}</span>
    <div class="form-select-wrapper">
      <select class="{{#if is-mobile}}mobile-{{/if}}currency-selector" data-model="CurrencySelector" dir="rtl">
        {{#each currency_selector.currencies}}
          <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}}>
            {{name}}
          </option>
        {{/each}}
      </select>
    </div>
  </div>
{{/if}}

