core/structured-data/breadcrumbs:
{{!-- Breadcrumbs: include in base.html --}}

<script id="schema-breadcrumbs">
  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": [
      {{#each breadcrumbs}}
        {
          "@type": "ListItem",
          "position": {{@index}} + 1,
          "item": {
            "@id": {{#unless @last}}"{{url}}"{{else}}window.location.href{{/unless}},
            "name": "{{name}}"
          }
        }{{#unless @last}},{{/unless}}
      {{/each}}
    ]
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-breadcrumbs');
  script.parentElement.removeChild(script);
</script>

pages/brand:
---
brand:
  products:
    limit: {{theme_settings.product_results_per_page}}
---

{{#partial "page"}}

  <header class="sub-header">
    {{> components/common/breadcrumbs breadcrumbs}}
  </header>

  <section class="layout-block product-grid">

    <div class="page-header">
      <h1 class="page-title centered">{{brand.name}}</h1>

      {{#if brand.image}}
        <div class="brand-header-logo">
          <img src="{{getImage brand.image 'thumbnail'}}" alt="{{brand.image.alt}}" />
        </div>
      {{/if}}

      {{{snippet 'brand'}}}
    </div>

    <div class="product-grid-container {{#if brand.faceted_search_enabled}}has-sidebar{{/if}}">

      {{#if brand.products.length}}
        {{#unless brand.faceted_search_enabled}}
          <div class="product-grid-sorting">
            {{> components/sorting sort=pagination.brand.sort}}
          </div>
        {{/unless}}

        {{#if brand.facets.length}}
          {{#if brand.faceted_search_enabled}}
            <div class="product-grid-sidebar">
              {{> components/sorting sort=pagination.brand.sort}}

              <div class="product-grid-filters">
                {{> components/brand/filters}}
              </div>
            </div>
          {{/if}}
        {{/if}}
      {{/if}}

      <div class="product-grid-list" data-quickshop>
        {{> components/brand/product-index}}
      </div>
    </div>

  </section>

{{/partial}}

{{> layout/base}}
